import React, { Component } from 'react';
import { connect } from 'react-redux';
import { isEmpty } from 'lodash';
import Table from '../table/Table';
import { getTeachers } from '../../actions/teachers';
import './Teachers.css';
import Moment from 'react-moment';

const headers = [
  {
    key: 'name',
    label: 'Name',
  },
  {
    key: 'surname',
    label: 'Surname',
  },
  {
    key: 'suspended',
    label: 'Suspended',
  },

  {
    key: 'date',
    label: 'Date created',
  },
  {
    key: 'actions',
    label: 'Actions',
  },
];
function formatYesNo(value) {
  return !value ? 'No' : 'Yes';
}
class Teachers extends Component {
  constructor(props) {
    super(props);
    this.state = {
      subjects: [],
    };
  }
  componentDidMount() {
    this.getTeachers();
  }

  componentDidUpdate(prevProps) {
    const { isLoading, suspended } = this.props;
    if (
      prevProps.isLoading &&
      !isLoading &&
      suspended &&
      !prevProps.suspended
    ) {
      this.getTeachers();
    }
  }

  getTeachers = () => {
    const { getTeachers } = this.props;
    getTeachers();
  };

  getTableOptions = () => {
    const options = {
      customComponents: {
        actions: {
          component: (data) => (
            <div className='subjects_actions'>
              <button
                className='btn btn-secondary'
                onClick={() =>
                  this.setState({
                    modalVisible: true,
                    selectedSubject: data,
                  })
                }
              >
                Request Appointment
              </button>
            </div>
          ),
        },
        date: {
          component: (rowData) => {
            return (
              <div>
                <Moment format='DD/MMM/YYYY hh:mm:ss'>
                  {rowData.datecreated}
                </Moment>
              </div>
            );
          },
        },
        suspended: {
          component: (rowData) => {
            return <div>{formatYesNo(rowData.suspended)}</div>;
          },
        },
      },
    };

    return options;
  };
  render() {
    const { teachers, isLoading } = this.props;

    return (
      <div className='subjects_table'>
        {isLoading && <div>Loading..</div>}
        {teachers && (
          <Table
            data={teachers}
            headers={headers}
            options={this.getTableOptions()}
          />
        )}
      </div>
    );
  }
}

const mapDispatchToProps = (dispatch) => ({
  getTeachers: () => dispatch(getTeachers()),
});

const mapStateToProps = (state) => ({
  teachers: state.teachers.teachers,
  isLoading: state.teachers.loading,
  suspended: state.teachers.suspended,
});

export default connect(mapStateToProps, mapDispatchToProps)(Teachers);
