import React, { Component } from 'react';
import Select from 'react-select';
import { Form, Button } from 'react-bootstrap';
import Axios from 'axios';
import './AddSubject.css';
import { getCategory } from '../../actions/category';

import { connect } from 'react-redux';

class AddSubject extends Component {
  constructor(props) {
    super(props);

    this.state = {
      title: '',
      description: '',
      priceperhour: '',
      timelimit: '',
      category: null,
    };
  }
  handleChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value,
    });
  };

  componentDidMount() {
    this.getCategory();
  }

  getCategory = () => {
    const { getCategory } = this.props;
    getCategory();
  };

  addNewSubjects = (event) => {
    event.preventDefault();
    Axios.post(
      '/subjects',
      {
        title: this.state.title,
        description: this.state.description,
        priceperhour: this.state.priceperhour,
        timelimit: this.state.timelimit,
        category: this.state.category.value,
      },
      { headers: { Authorization: 'myJwtToken' } }
    )
      .then((response) => {
        console.log('Subject successfully added.');
        alert('Subject successfully added.');
        this.props.history.push('/subjects');
      })
      .catch((error) => {
        console.log(error.response);
      })
      .finally(() => {
        console.log('Request completed.');
      });
  };
  mapOptions = () => {
    const { categories } = this.props;
    return categories.map((category) => ({
      label: category.title,
      value: category.title,
    }));
  };
  render() {
    const { categories } = this.props;
    return (
      <div className='add-subject'>
        {' '}
        <Form.Group>
          <Form.Label>Category:</Form.Label>

          {categories.length > 0 && (
            <Select
              options={this.mapOptions()}
              onChange={(category) => {
                this.setState({ category });
              }}
            />
          )}
        </Form.Group>
        <Form onSubmit={this.addNewProduct}>
          <Form.Group>
            <Form.Label>Title:</Form.Label>
            <Form.Control
              name='title'
              onChange={this.handleChange}
              type='text'
              placeholder='Enter subject title.'
            />
          </Form.Group>

          <Form.Group>
            <Form.Label>Description:</Form.Label>
            <Form.Control
              name='description'
              onChange={this.handleChange}
              type='text'
              placeholder='Enter subject description.'
            />
          </Form.Group>

          <Form.Group>
            <Form.Label>Price per hour:</Form.Label>
            <Form.Control
              name='priceperhour'
              onChange={this.handleChange}
              type='text'
              placeholder='What is the price per hour ?'
            />
          </Form.Group>

          <Form.Group>
            <Form.Label>Time Limit:</Form.Label>
            <Form.Control
              name='timelimit'
              onChange={this.handleChange}
              type='text'
              placeholder='What is the daily timelimit ?'
            />
          </Form.Group>

          <Button variant='success' type='submit' onClick={this.addNewSubjects}>
            Submit
          </Button>
        </Form>
      </div>
    );
  }
}
const mapDispatchToProps = (dispatch) => ({
  getCategory: () => dispatch(getCategory()),
});

const mapStateToProps = (state) => ({
  categories: state.category.category,
});
export default connect(mapStateToProps, mapDispatchToProps)(AddSubject);
