import React, { Fragment, useEffect } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Navbar from './components/layout/AppNavBar';
import Landing from './components/layout/Landing';
import Routes from './components/routing/Routes';
import Footer from './components/footer/Footer';

// Redux
import { Provider } from 'react-redux';
import store from './store';
import { loadUser } from './actions/auth';
import setAuthToken from './utils/setAuthToken';

import './App.css';

if (localStorage.token) {
  setAuthToken(localStorage.token);
}
var sectionStyle = {
  backgroundImage: `https://static.scientificamerican.com/sciam/cache/file/4E0744CD-793A-4EF8-B550B54F7F2C4406.jpg`,
};
const App = () => {
  useEffect(() => {
    store.dispatch(loadUser());
  }, []);

  return (
    <Provider store={store}>
      <Router>
        <Fragment>
          <div className='application'>
            <Navbar />

            <div className='container'>
              <div
                style={{
                  backgroundImage: `url("https://www.nicesnippets.com/image/imgpsh_fullsize.png")`,
                  backgroundRepeat: 'no-repeat',
                  width: '100%',
                  height: '100%',
                  color: 'white',
                }}
              >
                <Switch>
                  <Route exact path='/' component={Landing} />
                  <Route component={Routes} />
                </Switch>
              </div>
            </div>
            <Footer />
          </div>
        </Fragment>
      </Router>
    </Provider>
  );
};

export default App;
